import React from "react";
import styles from "./styles.module.scss";
import SubComment from './SubComment';

const Comment = ({ comment }: any) => (
  <div className={styles.comment}>
    <div className={styles.comment__user}>{comment.user}</div>
    <div className={styles.comment__content} dangerouslySetInnerHTML={{ __html: comment.content }}>
      {
        comment.comments ? (
          comment.comments.map((nested: any) => (
            <SubComment key={nested.id} comment={nested} />
          ))
        ) : null
      }
    </div>
  </div>
);

export default Comment;
